mapper.sql:
FROM ccg a
WHERE 1=1

	AND a.insertdate BETWEEN #{startDate} AND #{endDate}

AND ccgDelNy = #{shDelNy}
AND ccgUseNy = #{shUseNy}

	AND ccgSeq = #{shValue}
	AND ccgGroupNameKor LIKE CONCAT('%',#{shValue},'%')
	AND ccgGroupNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO ccg(
    ccgGroupNameEng
    ,ccgGroupNameKor
    ,ccgDelNy
    ,ccgUseNy
    ,ccgInsertDate
)
VALUES(
	#{ccgGroupNameEng}
    ,#{ccgGroupNameKor}
    ,#{ccgDelNy}
    ,#{ccgUseNy}
    ,now()

)

	SELECT last_insert_id()

mapper.update:
UPDATE ccg
SET
	ccgGroupNameKor = #{ccgGroupNameKor}
	,ccgGroupNameEng = #{ccgGroupNameEng}
	,ccgUseNy = #{ccgUseNy}
	,ccgDelNy = #{ccgDelNy}
	,ccgModDate = now()
WHERE
	ccgSeq = #{ccgSeq}
