mapper.select:
select b.* ,a.* from ccg a join cc b on b.ccg_seq = a.ccgSeq
AND UseNY = #{shDelNy}
AND ccg_seq = #{shValue} AND cc_Name LIKE CONCAT('%',#{shValue},'%') AND ifcgNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO cc(
	cc_name
	,cc_nameEng
	,cc_useNy
	,cc_delNy
	,ccg_seq
)
VALUES(
	#{cc_name}
	,#{cc_nameEng}
	,#{cc_useNy}
	,#{cc_delNy}
	,#{ccg_seq}
)

	SELECT last_insert_id()
