mapper.select:
SELECT COUNT(*) FROM cc a WHERE 1=1
AND a.cc_InsertDate Between #{startDate} AND #{endDate}
AND ccSeq = #{shValue} AND cc_name LIKE CONCAT('%',#{shValue},'%') AND cc_nameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO cc(
	cc_name
	,cc_nameEng
	,cc_useNy
	,cc_delNy
	,ccg_seq
)
VALUES(
	#{cc_name}
	,#{cc_nameEng}
	,#{cc_useNy}
	,#{cc_delNy}
	,#{ccg_seq}
)

	SELECT last_insert_id()
