mapper.select:
select
	a.*
    ,(select count(ccg_seq) from cc aa where aa.ccg_seq = a.ccgSeq ) as countCCG
from ccg a
where 1=1 
	 AND a.insertdate Between #{startDate} AND #{endDate}
	AND UseNY = #{shDelNy}

	AND ccgSeq = #{shValue}
	AND ccg_groupname LIKE CONCAT('%',#{shValue},'%')
	AND ccg_groupnameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO ccg(
    ccg_groupname
    ,delNy
    ,UseNY
    ,ccg_groupnameEng
    ,insertdate
)
VALUES(
	#{ccg_groupname}
	,#{delNy}
	,#{UseNY}
	,#{ccg_groupnameEng}
	,now()
)

	SELECT last_insert_id()
