mapper.insert:
INSERT INTO question(
	content
	,userID
	,writetime
	,language_select
)
VALUES(
	#{content}
	,#{userID}
	,now()
	,#{language_select}
)

	SELECT last_insert_id()

INSERT INTO answer(
	ansContent
	,ansUserId
	,ansWriteTime
	,ansQuestionSeq
)
VALUES(
	#{ansContent}
	,#{ansUserId}
	,now()
	,${questionSeq}
)

	SELECT last_insert_id()

INSERT INTO bookmark(
	bookmark_useNy
	,question_questionSeq
	,bookmark_UserId
)
VALUES(
	1
	,#{question_questionSeq}
	,#{bookmark_UserId}
)

		SELECT last_insert_id()

INSERT INTO answer_like(
	likeUseNy
	,likeAnswerSeq
	,likeTime
	,likeUserId
)
VALUE(
	1
	,#{likeAnswerSeq}
	,now()
	,#{likeUserId}
	)

		SELECT last_insert_id()

mapper.update:
UPDATE question
SET
	content = #{content}
WHERE
	questionSeq = #{questionSeq}
